magic_number = 23# Loop through a range of numbers to try & find the magic number.print('Loop without a Break statement:')for n in range(101):	if n is magic_number:		# Whenever printing an integer and a string, separate them with a comma.		print(n, 'is the magic number!')	# Although the program finds the magic number, the program continues looping thro' the range.	print(n)# Use a break statement to stop the loop whenever the magic number is found.print('Loop with a Break statement:')num = 27for i in range(80):	if i is num:		print(i, 'is the magic number!')		break	else:		print(i)