import random# Use input() function to collect input from the keyboard.x = int(input('Enter a number btwn 1-20: '))# Random allows a program generate numbers at random.def magic_number():	# Ensure the number users enter is in your range.	if x not in range(1, 20):		print('OUT OF SCOPE!')	elif x in range(1, 20):		y = random.randrange(1, 20)		if x is y:			print('YOU WON!', y)		else:			print('YOU LOST!', y)	else:		print('TRY AGAIN!!')magic_number()