from selenium import webdriverfrom selenium.webdriver.common.keys import Keysimport time"""A program to log in to Instagram Account, using Selenium & geckoDriver.The init function gets called automatically whenever an object of the class is created."""class InstaBot:	# Whenever we create an object, we need to pass in the username & password.	def __init__(self, username, password):		self.username = username		self.password = password		self.driver = webdriver.Chrome()	def closebrowser(self):		self.driver.close()	# Function to login.	def login(self):		driver = self.driver		driver.get('https://instagram.com/')		time.sleep(2)		# "//a[@href'accounts/login']"		# Find the login link & click it.		login_btn = driver.find_element_by_xpath("//a[@href='/accounts/login/?source=auth_switcher']")		login_btn.click()		time.sleep(2)		# Find the username & password fields and enter the values passed in in the object.		user_name = driver.find_element_by_xpath("//input[@name='username']")		user_name.clear()		user_name.send_keys(self.username)		pass_word = driver.find_element_by_xpath("//input[@name='password']")		pass_word.send_keys(self.password)		# Hit enter to login.		pass_word.send_keys(Keys.RETURN)		time.sleep(2)# Create an object to use the class.ob = InstaBot('username', 'password!')ob.login()